$ADComputer = Get-ADComputer -Filter * $data = foreach ($Computer in $ADComputer) {    $query = Get-CimInstance -ClassName "Win32_BIOS" -ComputerName $Computer.Name | Select *    $sn = $query.SerialNumber    $manufacturer = $query.Manufacturer    $PC = $query.PSComputerName    [PSCustomObject][ordered]@{    PC = $PC    Manufacturer = $manufacturer    SN = $sn    }    }$data | Export-Csv -Path "C:\Temp\Export.csv" -NoTypeInformation -Delimiter "," -Encoding UTF8